type.raw:
chainPropTypes(PropTypes.string, props => {
  if (!props.readOnly && !props.name) {
    return new Error(
      [
        'Material-UI: the prop `name` is required (when `readOnly` is false).',
        'Additionally, the input name should be unique within the parent form.',
      ].join('\n'),
    );
  }
  return null;
})
